import React, { useState } from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
   const [movies, setMovies] = useState([])
   const [isLoading, setLoading] = useState(false)
   const [error, setError] = useState(null)

  // const dummyMovies = [
  //   {
  //     id: 1,
