import React,{useState} from "react";

const Input =()=>{
const [title, setTitle] = useState('');

const titleHandler = (event) =>{
    console.log(event.title.value)
}

  return (
    <>
    <form>
    <label>Title
        <input type="text" onChange={titleHandler}   />
    </label>
    <br />
    <label>Opening Text
        <input type="text" />
    </label>
    <br />
    <label>Release Date
        <input type="date" />
    </label>
    </form>


    <button>Add Movie</button>
    </>
  )
}

export default Input;
